sys_ui_action.script:
function getConfigItems() {
	var sys_ids = g_list.getChecked().toString();
	var sys_id_list = sys_ids.split(",");
	var ci_names = [];

	try {
		if(sys_id_list.length > 1) {
			var ciGr = new GlideRecord('cmdb_ci');
			ciGr.addQuery('sys_id', 'IN', sys_id_list);
			ciGr.query();

			while(ciGr.next()) {
				var res = ciGr.name.toString().trim() || ciGr.serial_number.toString().trim() || ciGr.sys_id.toString().trim();
				ci_names.push(res);
			}

			var confirmationMessage = 'Selected CI\'s (' + sys_id_list.length + '):\n\n';
			confirmationMessage += ci_names.join('\n');
			confirmationMessage += '\n\nClick OK to create de-duplication task for these CI\'s.';

			var con0 = confirm(confirmationMessage);

			if(con0) {
				var ga = new GlideAjax('CMDBDeduplicationTaskUtils');
				ga.addParam('sysparm_name', 'createDeduplicationTask');
				ga.addParam('sysparm_sysids', sys_ids);
				ga.getXML(function(deDupAjax0Response) {
					var answer = deDupAjax0Response.responseXML.documentElement.getAttribute('answer');

					if(answer == null) {
						alert('Failed to create De-duplication task. Selected CI\'s are already included in an open De-Duplication task!');
					} else {
						var uri = 'reconcile_duplicate_task.do?sys_id=' + answer;
						var con1 = confirm('The De-duplication task has been created. Click OK to view the task record.');

						if(con1) location.href= uri;
					}
				});
			}
		} else {
			alert('You must select at least two CI\'s!');
		}
	} catch(e) {
		alert('An error has occurred. Pleaese contact your administrator\n\n' + JSON.stringify(e.message));
	}
}
